<?php/* Template Name: FB Step 1 Create */get_header();session_unset();?><?php // $page_background_photo = getOccasionBGImg(0); ?><?php if ( have_posts() ) : while ( have_posts() ) : the_post(); ?><main id="main" role="main">	<div class="container">		<div class="heading">			<h1><?php the_field('page_title'); ?></h1>		</div>		<ul class="gallery-holder">		<?php		$args = array(		    'orderby'           => 'slug',		    'order'             => 'ASC',		    'hide_empty'        => false		);			$terms = get_terms("flatterbox_type",$args);			$count = count($terms);			if ( $count > 0 ){			     foreach ( $terms as $term ) {			     ?>			<li><a href="<?php echo do_shortcode('[site_url]'); ?>/choose-card?type=<?php echo $term->term_id; ?>&typename=<?php echo $term->name; ?>">				<?php $imageURL = z_taxonomy_image_url($term->term_id);				$imageURLExtension = substr($imageURL, -4);				$imageURLBase = substr($imageURL, 0, -4);				$imageURLRecalc = ''.$imageURLBase.'-290x290'.$imageURLExtension; ?>				<div class="img-holder">					<img src="<?php echo $imageURLRecalc; ?>" height="290" width="290" alt="<?php echo $imageURLRecalc; ?>">					<span class="box"><?php echo $term->name; ?></span>				</div></a>			</li>				<?php			     }	 	}	 	?>		</ul>	</div></main><?php endwhile; endif; ?><?php get_footer(); ?>